    const [Monday, SetMonday] = useState(completed);
    //const [updateTask,SetUpdateTask] = useState(updateTask)
    const updateTask = (e)=> {
        console.log(e.target.value)
        SetMonday(!Monday);
        const tesitngUrl  = 'api/add/';
        const postOptions = {
            method: 'POST',
            body: {
                'title':title,
                'completed': !Monday
              }
        };
        sendReq(tesitngUrl,postOptions)
            //.then((data)=>console.log('Sent!'));
        //window.location.reload();
    };

    return(
        <div className='flex'>
            <p className='p-2'>{title}</p>
            <input type="checkbox" checked={Monday} onClick={updateTask}></input>
        </div>
    )
}
export default Task
