import sendReq from '../../sendReq';
import { useState, useRef, useEffect } from "react";


function Task({ completed, title }){
    const [Monday, SetMonday] = useState(completed);
    return(
        <div className='flex'>
            <p className='p-2'>{title}</p>
            <input type="checkbox" checked={Monday} onClick={() => SetMonday(!Monday)}></input>
        </div>
    )
}
export default Task
