import { useState, useRef } from "react";
import emailjs from '@emailjs/browser';
function Contact(){
    const [name,setName] = useState('');
    const [email,setEmail] = useState('');
    const [message,setMessage] = useState('');
    const [subject,setSubject] = useState('');
    const [success, setSuccess] = useState(false);
    const form = useRef();

    const SendEmail = (e) =>{
        e.preventDefault();
        console.log(form.current);

        emailjs.sendForm('service_i1why2k', 'template_trmmfms', form.current, '-H9zs88_sbBksYcow')
        .then((result) => {
            console.log(result.text);
            setName('');
            setEmail('');
            setMessage('');
            setSubject('');
            setSuccess(true);

        }, (error) => {
            console.log(error.text);
        });


    };

    return (
        <div className='text-amber-600 ml-32 w-2/3'>
            <form ref={form} onSubmit={SendEmail}>
                <input
                className='w-full p-6 my-8 bg-gray-900'
                placeholder='Your Name'
                value = {name}
                name = 'Name'
                onChange={(e)=> setName(e.target.value)}
                ></input>

                <input
                className='w-full p-6 my-8 bg-gray-900'
                placeholder='Your Email'
                value = {email}
                name = 'Email'
                onChange={(e)=> setEmail(e.target.value)}
                ></input>

                <input
                className='w-full p-6 my-8 bg-gray-900'
                placeholder='Email Subject'
                value = {subject}
                name = 'Subject'
                onChange={(e)=> setSubject(e.target.value)}
                ></input>

                <textarea
                className='w-full p-6 my-8 bg-gray-900'
                placeholder='Your Message'
                value = {message}
                name = 'Message'
                onChange={(e)=> setMessage(e.target.value)}
                ></textarea>

                <input 
                className='w-full p-6 my-8 bg-gray-900'
                type='submit' 
                className='text-white'  
                value = 'SendMEssage' 
                onChange={(e)=> setName(e.target.value)}
                ></input>
            </form>
            {success ? (<div>EMail was sent!</div>): (<div>EMail was failed!</div>)}

        </div>
    )

}
export default Contact;
