import sendReq from '../sendReq';
import { useState, useRef, useEffect } from "react";
import Task from './todo/task'
function TestAPI(){
    const [Tasks,setTasks] = useState(null);
    const [newTitle,setNewTitle] = useState('')
    const [updateTask,SetUpdateTask] = useState(false);

    const addTask = (e)=> {
        const tesitngUrl  = 'api/add/';
        const bod = {
            'title':newTitle,
            'monday': false,
            'tuesday': false,
            'wednesday': false,
            'thursday': false,
            'friday': false,
            'saturday': false,
            'sunday': false
          };
        const postOptions = {
            method: 'POST',
            body: bod
        };
        sendReq(tesitngUrl,postOptions)
            //.then((data)=>console.log('Sent!'));
        //window.location.reload();
        setNewTitle('');
    };


    useEffect (() =>{
        const tesitngUrl  = 'api/add/';
        const getOptions = {
            method: "GET"
        }
        sendReq(tesitngUrl,getOptions)
        .then((data)=>{
            const allTasks = data.data.map((task) =>{
                return (<div key={task.id} >
                    <Task 
                    mon={task.monday} 
                    tues = {task.tuesday}
                    wed = {task.wednesday}
                    thurs = {task.thursday}
                    fri = {task.friday}
                    sat = {task.saturday}
                    sun = {task.sunday}
                    title = {task.title}>

                    </Task>
                    </div>)
            })
            //console.log(allTasks)
            setTasks(allTasks)
        });
    },[])


    return (
        <div className='text-white'>

            <button onClick={onclick}>TESTING API</button>
            {Tasks}
            <form>
                <label>Add Task</label>
                <input placeholder='Title' className='text-black' onChange={(e)=>setNewTitle(e.target.value)} value = {newTitle}></input>
                <button>Click to add the Task</button>
            </form>
        </div>
    )
}
export default TestAPI;
