import sendReq from '../sendReq';
import { useState, useRef, useEffect } from "react";
import Task from './todo/task'
function TestAPI(){
    const [Tasks,setTasks] = useState(null);
    const [newTitle,setNewTitle] = useState('')
    const [updateTask,SetUpdateTask] = useState(false);
    const onclick = ()=> {
        SetUpdateTask(true);
        console.log('HERE')
    };

    useEffect (() =>{
        const tesitngUrl  = 'api/add/';
        const getOptions = {
            method: "GET"
        }
        sendReq(tesitngUrl,getOptions)
        .then((data)=>{
            const allTasks = data.data.map((task) =>{
                return (<div key={task.id} >
                    <Task completed={task.completed} title = {task.title}></Task>
                    </div>)
            })
            console.log(allTasks)
            setTasks(allTasks)
        });
    },[])


    return (
        <div className='text-white'>

            <button onClick={onclick}>TESTING API</button>
            {Tasks}
            <form>
                <input placeholder='Title' className='text-black' onChange={(e)=>setNewTitle(e.target.value)} value = {newTitle}></input>
                <input placeholder='Title' className='text-black' onChange={(e)=>setNewTitle(e.target.value)} value = {newTitle}></input>
                <input type='checkbox' className='text-black'></input>
            </form>
        </div>
    )
}
export default TestAPI;
