import sendReq from '../sendReq';
import { useState, useRef, useEffect } from "react";
function TestAPI(){
    const [Tasks,setTasks] = useState(null);
    const onclick = ()=> {
        const tesitngUrl  = 'api/todos/';
        const postOptions = {
            method: 'POST',
            body: {
                'description': 'Do test',
                'title':'do test',
                'completed': true
              }
        };
        sendReq(tesitngUrl,postOptions)
            .then((data)=>console.log(data.data));
    };

    useEffect (() =>{
        const tesitngUrl  = 'api/todos/';
        const getOptions = {
            method: "GET"
        }
        sendReq(tesitngUrl,getOptions)
        .then((data)=>{
            console.log(data.data);
            const allTasks = data.data.map((ob,i) =>{
                return (<div>{ob.title}</div>)
            })
            console.log(allTasks)
            setTasks(allTasks)
        });
    },[])


    return (
        <div className='text-white'>

            <button onClick={onclick}>TESTING API</button>
            {Tasks}
        </div>
    )
}
export default TestAPI;
