import sendReq from '../../sendReq';
import { useState, useRef, useEffect } from "react";


function Task({ mon, tues, wed, thurs, fri, sat, sun, title }){
    const [Monday, SetMonday] = useState(mon);
    const [Tuesday, SetTuesday] = useState(tues);
    const [Wednesday, SetWednesday] = useState(wed);
    const [Thursday, SetThursday] = useState(thurs);
    const [Friday, SetFriday] = useState(fri);
    const [Saturday, SetSaturday] = useState(sat);
    const [Sunday, SetSunday] = useState(sun);
    //const [updateTask,SetUpdateTask] = useState(updateTask)
    const updateTask = (e)=> {
        const tesitngUrl  = 'api/add/';
        const postOptions = {
            method: 'POST',
            body: {
                'title':title,
                'completed': e.target.checked
              }
        };
        sendReq(tesitngUrl,postOptions)
            //.then((data)=>console.log('Sent!'));
        //window.location.reload();
    };

    return(
        <div className='flex'>
            <p className='p-2'>{title}</p>
            <input type="checkbox" checked={Monday} onClick={() => SetMonday(!Monday)}></input>
            <input type="checkbox" checked={Tuesday} onClick={() => SetMonday(!Tuesday)} ></input>
            <input type="checkbox" checked={Wednesday} onClick={() => SetMonday(!Wednesday)} ></input>
            <input type="checkbox" checked={Thursday} onClick={() => SetMonday(!Thursday)} ></input>
            <input type="checkbox" checked={Friday} onClick={() => SetMonday(!Friday)} ></input>
            <input type="checkbox" checked={Saturday} onClick={() => SetMonday(!Saturday)} ></input>
            <input type="checkbox" checked={Sunday} onClick={() => SetMonday(!Sunday)}></input>
        </div>
    )
}
export default Task
