import { useState, useRef, useEffect } from "react";
function Score({ data }){
    const [mon,setmon] = useState(0);
    const [tues,settues] = useState(0);
    const [wed,setwed] = useState(0);
    const [thur,setthur] = useState(0);
    const [fri,setfri] = useState(0);
    const [sat,setsat] = useState(0);
    const [sun,setsun] = useState(0);
    useEffect (() => {
        let curmon = 0;
        let curtues = 0;
        let curwed = 0;
        let curthur = 0;
        let curfri = 0;
        let cursat = 0;
        let cursun = 0;
        data.map((task)=>{
            curmon+=task.monday
            curtues+=task.tuesday
            curwed+=task.wednesday
            curthur+=task.thursday
            curfri+=task.friday
            cursat+=task.saturday
            cursun+=task.sunday
        });
        setmon(curmon/data.length);
        settues(curtues/data.length);
        setwed(curwed/data.length);
        setthur(curthur/data.length);
        setfri(curfri/data.length);
        setsat(cursat/data.length);
        setsun(cursun/data.length);
    });

    const calcScore = () =>{

        window.location.reload(false);
    }


    return (
        <div>

            <div className='flex justify-between border-t-2 border-amber-900'>
                <button onClick={calcScore} className='w-32 whitespace-nowrap p-2' className=''>Refresh Score!</button>
                <p>{mon}</p>
                <p>{tues}</p>
                <p>{wed}</p>
                <p>{thur}</p>
                <p>{fri}</p>
                <p>{sat}</p>
                <p>{sun}</p>
            </div>

        </div>
    )
}
export default Score;
