import sendReq from '../../sendReq';
import { useState, useRef, useEffect } from "react";


function Task({ mon, tues, wed, thurs, fri, sat, sun, title }){
    const [Monday, SetMonday] = useState(mon);
    const [Tuesday, SetTuesday] = useState(tues);
    const [Wednesday, SetWednesday] = useState(wed);
    const [Thursday, SetThursday] = useState(thurs);
    const [Friday, SetFriday] = useState(fri);
    const [Saturday, SetSaturday] = useState(sat);
    const [Sunday, SetSunday] = useState(sun);
    //const [updateTask,SetUpdateTask] = useState(updateTask)
    const updateTask = (e)=> {
        const tesitngUrl  = 'api/add/';
        const bod = {
            'title':title,
            'monday': Monday,
            'tuesday': Tuesday,
            'wednesday': Wednesday,
            'thursday': Thursday,
            'friday': Friday,
            'saturday': Saturday,
            'sunday': Sunday
          };
        console,log(e.target.name)
        bod[e.target.name] = !bod[e.target.name];
        const postOptions = {
            method: 'POST',
            body: bod
        };
        sendReq(tesitngUrl,postOptions)
            //.then((data)=>console.log('Sent!'));
        //window.location.reload();
    };

    return(
        <div className='flex'>
            <p className='p-2'>{title}</p>
            <input type="checkbox" checked={Monday} onClick={() => {SetMonday(!Monday); updateTask();}} name='monday'></input>
            <input type="checkbox" checked={Tuesday} onClick={() => SetTuesday(!Tuesday)} name='tuesday'></input>
            <input type="checkbox" checked={Wednesday} onClick={() => SetWednesday(!Wednesday)} name='wednesday' ></input>
            <input type="checkbox" checked={Thursday} onClick={() => SetThursday(!Thursday)} name='thursday'></input>
            <input type="checkbox" checked={Friday} onClick={() => SetFriday(!Friday)} name='friday'></input>
            <input type="checkbox" checked={Saturday} onClick={() => SetSaturday(!Saturday)} name='saturday'></input>
            <input type="checkbox" checked={Sunday} onClick={() => SetSunday(!Sunday)} name='sunday'></input>
            <button onClick={updateTask}>SAVE</button>
        </div>
    )
}
export default Task
