import { useState, useEffect } from "react";

function Weathercall({ data }) {
    const [dataForcast,setDataForcast] = useState({});

    let sunrise;
    let sunset;
    let weatherIcon;
    if (data.hasOwnProperty('main')){
        sunrise = (new Date(data.sys.sunrise*1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }));
        sunset  = (new Date(data.sys.sunset*1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }));
        weatherIcon = `http://openweathermap.org/img/w/${data.weather[0].icon}.png`;

    }


    return (
        <div className='pt-8 pl-12'>
            <div className='pl-4'>
                <p className='text-4xl'>{data.name ? String(data.name)+', ' +String(data.sys.country) :null}</p>
                <p className='text-2xl'>{data.dt ? (new Date(data.dt*1000).toDateString()):null}</p>
            </div>


            <div className='flex py-6 w-full bg-slate'>
                <div className='px-6'>
                    {weatherIcon ? <img src={weatherIcon}></img>:null}
                    <p>{data.main ? (data.weather[0].description) : 'loading'}</p>

                </div>

                <div className=''>
                    <p>{data.main ? (data.main.temp) : 'loading'}</p>
                    <p>High: {data.main ? (data.main.temp_max) : 'loading'}</p>
                    <p>Low: {data.main ? (data.main.temp_min) : 'loading'}</p>
                    <p className='text-2xl'>Sunrise: {sunrise ? sunrise :'loading'}</p>
                    <p className='text-2xl'>Sunset: {sunset ? sunset:'loading'}</p>
                </div>


            </div>



        </div>
    )
}
export default Weathercall;
