import sendReq from '../../sendReq';
import { useState, useRef, useEffect } from "react";


function Task({ mon, tues, wed, thurs, fri, sat, sun, title }){
    const [Monday, SetMonday] = useState(completed);
    const [Tuesday, SetTuesday] = useState(completed);
    const [Wednesday, SetWednesday] = useState(completed);
    const [Thursday, SetThursday] = useState(completed);
    const [Friday, SetFriday] = useState(completed);
    const [Saturday, SetSaturday] = useState(completed);
    const [Sunday, SetSunday] = useState(completed);
    //const [updateTask,SetUpdateTask] = useState(updateTask)
    const updateTask = (e)=> {
        //console.log(e.target.name)
        SetMonday(!Monday);
        const tesitngUrl  = 'api/add/';
        const postOptions = {
            method: 'POST',
            body: {
                'title':title,
                'completed': e.target.checked
              }
        };
        sendReq(tesitngUrl,postOptions)
            //.then((data)=>console.log('Sent!'));
        //window.location.reload();
    };

    return(
        <div className='flex'>
            <p className='p-2'>{title}</p>
            <input type="checkbox" checked={Monday} onClick={updateTask} name='Monday'></input>
        </div>
    )
}
export default Task
