import sendReq from '../sendReq';
import { useState, useRef, useEffect } from "react";
function TestAPI(){
    const [Tasks,setTasks] = useState(null);
    const onclick = ()=> {
        const tesitngUrl  = 'api/todos/';
        const postOptions = {
            method: 'POST',
            body: {
                'description': 'Do test',
                'title':'do test',
                'completed': true
              }
        };
        sendReq(tesitngUrl,postOptions)
            .then((data)=>console.log(data.data));
        window.location.reload();
    };

    useEffect (() =>{
        const tesitngUrl  = 'api/todos/';
        const getOptions = {
            method: "GET"
        }
        sendReq(tesitngUrl,getOptions)
        .then((data)=>{
            const allTasks = data.data.map((task) =>{
                return (<div key={task.id} className='flex'>
                    <p className='p-2'>{task.title}</p>
                    <p className='p-2'>{task.description}</p>
                    <p className='p-2'>{task.completed ? 'true':'false'}</p>


                    </div>)
            })
            console.log(allTasks)
            setTasks(allTasks)
        });
    },[])


    return (
        <div className='text-white'>

            <button onClick={onclick}>TESTING API</button>
            {Tasks}
        </div>
    )
}
export default TestAPI;
