import sendReq from '../sendReq';
import { useState, useRef, useEffect } from "react";
import Task from './todo/task'
import Score from './todo/score';
function TestAPI(){
    const [Tasks,setTasks] = useState(null);
    const [newTitle,setNewTitle] = useState('')
    const [updateTask,SetUpdateTask] = useState(false);
    const current = new Date();
    //const date = `${current.getDate()}/${current.getMonth()+1}/${current.getFullYear()}`;
    const date = `${current.toLocaleString('default',{month:'long'})} ${current.getDate()}, ${current.getFullYear()}`
    const [score, setScore] = useState(null);

    const addTask = (e)=> {
        const tesitngUrl  = 'api/add/';
        console.log(newTitle)
        const bod = {
            'title':newTitle,
            'monday': false,
            'tuesday': false,
            'wednesday': false,
            'thursday': false,
            'friday': false,
            'saturday': false,
            'sunday': false
          };
        const postOptions = {
            method: 'POST',
            body: bod
        };
        sendReq(tesitngUrl,postOptions)
            //.then((data)=>console.log('Sent!'));
        //window.location.reload();
        setNewTitle('');
    };


    useEffect (() =>{
        const tesitngUrl  = 'api/add/';
        const getOptions = {
            method: "GET"
        }
        sendReq(tesitngUrl,getOptions)
        .then((data)=>{
            setScore(<Score data = {data.data}></Score>)
            const allTasks = data.data.map((task) =>{
                return (<div key={task.id} >
                    <Task 
                    mon={task.monday} 
                    tues = {task.tuesday}
                    wed = {task.wednesday}
                    thurs = {task.thursday}
                    fri = {task.friday}
                    sat = {task.saturday}
                    sun = {task.sunday}
                    title = {task.title}>

                    </Task>
                    </div>)
            })
            //console.log(allTasks)
            setTasks(allTasks)
        });
    },[])


    return (
        <div className='px-32 py-4 text-amber-400 h-screen'>
            <div className='bg-amber-700 rounded-lg pl-2 text-xl w-32'>
                <p>Hi, today is </p>
                <p className=''>{date}</p>
            </div>


            <div className=''>
                <div className='flex justify-between '>
                    <p className='w-32'>Daily Tasks</p>
                    <p>Monday</p>
                    <p>Tuesday</p>
                    <p>Wednesday</p>
                    <p>Thursday</p>
                    <p>Friday</p>
                    <p>Saturday</p>
                    <p>Sunday</p>
                </div>
                {Tasks}
                {score}
            </div>

            <form className='py-8'>
                <input placeholder='Add Task Here' className=' pl-2 text-black rounded-lg mr-8' onChange={(e)=>setNewTitle(e.target.value)} value = {newTitle}></input>
                <button onClick={addTask} className='bg-slate-700 px-4 bg-opacity-60 ml-1 rounded-lg  hover:bg-opacity-40'>Submit</button>
            </form>






        </div>
    )
}
export default TestAPI;
