import sendReq from '../../sendReq';
import { useState, useRef, useEffect } from "react";


function Task({ completed, title }){
    const [Monday, SetMonday] = useState(completed);
    const updateTask = ()=> {
        const tesitngUrl  = 'api/add/';
        const postOptions = {
            method: 'POST',
            body: {
                'title':'do test',
                'completed': false
              }
        };
        sendReq(tesitngUrl,postOptions)
            .then((data)=>console.log(data.data));
        window.location.reload();
    };
    return(
        <div className='flex'>
            <p className='p-2'>{title}</p>
            <input type="checkbox" checked={Monday} onClick={() => SetMonday(!Monday)}></input>
        </div>
    )
}
export default Task
