    const weeklyUrl = '/api/weekly/';
    const [bod,setBod] = useState(null);
    const getOptions = {
        method: "GET"
    }
    sendReq(weeklyUrl,getOptions)
        .then((data)=>{
            const allWeeklyTasks = data.data.map((task)=>{
                <div key = {task.id}>
                    <p className='w-24 whitespace-nowrap p-2'>{task.title}</p>
                </div>
            })
        })

    return (
        <div>
            {bod}
        </div>
    )


};
export default WeeklyData;
